    private void Button_SigninActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Button_SigninActionPerformed
        UserDAO userDAO = new UserDAO();
        String username = TextField_Name.getText();
        String password = PasswordField_Password.getText();
        
        UserModel usermodel = userDAO.selectUser(username);
        if(username.equals("") && password.equals("")){
            JOptionPane.showMessageDialog(this,"Bạn hãy đăng nhập!");
            return;
        }
        if(usermodel == null){
            JOptionPane.showMessageDialog(this,"Tài khoản không tồn tại");
            return;
        }
        if(usermodel.getPassWord().equals(password)){
            EnglishFunnyFrame.scene.user = usermodel;
            EnglishFunnyFrame.scene.mainmenu.Init();
            
        }
        
        else{
            JOptionPane.showMessageDialog(this,"Tài khoản không tồn tại");
            return;
        }
        
    }//GEN-LAST:event_Button_SigninActionPerformed

    private void Button_SignupMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Button_SignupMouseClicked
        EnglishFunnyFrame.scene.signin.setVisible(false);
        EnglishFunnyFrame.scene.signup.setVisible(true);
        EnglishFunnyFrame.scene.signup.Init();
        
              
    }//GEN-LAST:event_Button_SignupMouseClicked

