    private void Button_SigninMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Button_SigninMouseClicked
        JOptionPane.showMessageDialog(this, "Đăng nhập thành công!");
        EnglishFunnyFrame.scene.welcomeLEFT.setVisible(false);
        EnglishFunnyFrame.scene.signin.setVisible(false);
        EnglishFunnyFrame.scene.mainmenu.setVisible(true);
        EnglishFunnyFrame.scene.chart.setVisible(true);
        
    }//GEN-LAST:event_Button_SigninMouseClicked
